const bodyParser = require('body-parser');
require('dotenv').config();
var express = require('express');
var app = express();
app.use(express.static('Public'));
const mongoose = require('mongoose');
const morgan = require('morgan');
const jwt = require('jsonwebtoken');

const {DATABASE_URL, PORT} = require('./config');
const {Business} = require('./models');
const {JWT_SECRET} = require('./config');
const passport = require('passport');
const {BasicStrategy} = require('passport-http');
const {
    // Assigns the Strategy export to the name JwtStrategy using object
    // destructuring
    // https://developer.mozilla.org/en/docs/Web/JavaScript/Reference/Operators/Destructuring_assignment#Assigning_to_new_variable_names
    Strategy: JwtStrategy,
    ExtractJwt
} = require('passport-jwt');
const createAuthToken = user => {
    return jwt.sign({user}, JWT_SECRET, {
        subject: user.username,
        expiresIn: '7d',
        algorithm: 'HS256'
    });
};
const jwtStrategy = new JwtStrategy(
    {
        secretOrKey: JWT_SECRET,
        // Look for the JWT as a Bearer auth header
        jwtFromRequest: ExtractJwt.fromAuthHeaderWithScheme('Bearer'),
        // Only allow HS256 tokens - the same as the ones we issue
        algorithms: ['HS256']
    },
    (payload, done) => {
        done(null, payload.user);
    }
);
const basicStrategy = new BasicStrategy((username, password, callback) => {
    let user;
    Business.findOne({username: username})
        .then(_user => {
            user = _user;
            if (!user) {
                // Return a rejected promise so we break out of the chain of .thens.
                // Any errors like this will be handled in the catch block.
                return Promise.reject({
                    reason: 'LoginError',
                    message: 'Incorrect username or password'
                });
            }
            return user.validatePassword(password);
        })
        .then(isValid => {
            if (!isValid) {
                return Promise.reject({
                    reason: 'LoginError',
                    message: 'Incorrect username or password'
                });
            }
            return callback(null, user);
        })
        .catch(err => {
            if (err.reason === 'LoginError') {
                return callback(null, false, err);
            }
            return callback(err, false);
        });
});
app.use(passport.initialize());
passport.use(basicStrategy);
passport.use(jwtStrategy);
app.use(morgan('common'));

app.use(bodyParser.json());

mongoose.Promise = global.Promise;

app.post(
    '/login',
    // The user provides a username and password to login
    passport.authenticate('basic', {session: false}),
    (req, res) => {
        const authToken = createAuthToken(req.user);
        res.json({authToken});
    }
);
app.get('/certificate', passport.authenticate('jwt', {session: false}), (req, res) => {
     res.json("");
});
app.get('/businesses', (req, res) => {
  Business
    .find()
    .then(businesses => {
      res.json(businesses);
    })
    .catch(err => {
      console.error(err);
      res.status(500).json({error: 'something went terribly wrong'});
    });
});
app.post('/businesses', (req, res) => {
    console.log(req);
  const requiredFields = ['username', 'password', 'name', 'address'];
  for (let i=0; i<requiredFields.length; i++) {
    const field = requiredFields[i];
    if (!(field in req.body)) {
      const message = `Missing \`${field}\` in request body`;
      console.error(message);
      return res.status(400).send(message);
    }
  }

  Business.hashPassword(req.body.password).then(password=>
    Business.create({
      username: req.body.username,
      password: password,
      name: req.body.name,
      address: req.body.address
    })
    )
    .then(business => res.status(201).json(business))
    .then( passport.authenticate('jwt', {session: false}),
    (req, res) => {
        const authToken = createAuthToken(req.business);
        res.json({authToken});
    })
    .catch(err => {
        console.error(err);
        res.status(500).json({error: 'Something went wrong'});
    });
});
let server;

function runServer(databaseUrl=DATABASE_URL, port=PORT) {
  return new Promise((resolve, reject) => {
    mongoose.connect(databaseUrl, err => {
      if (err) {
        return reject(err);
      }
      server = app.listen(port, () => {
        console.log(`Your app is listening on port ${port}`);
        resolve();
      })
      .on('error', err => {
        mongoose.disconnect();
        reject(err);
      });
    });
  });
}

function closeServer() {
    return mongoose.disconnect().then(() => {
        return new Promise((resolve, reject) => {
            console.log('Closing server');
            server.close(err => {
                if (err) {
                    return reject(err);
                }
                resolve();
            });
        });
    });
}

if (require.main === module) {
    runServer().catch(err => console.error(err));
}

module.exports = {app, runServer, closeServer, jwtStrategy};
